use Utemtervgocreate function keszTermekE(	@part int)returns bitasbegin	declare @dbElofordulas int = 0;	select @dbElofordulas = COUNT(*)	from PS_MSTR	where ps_comp = @part	if(@dbElofordulas = 0)	begin		return 1;	end		return 0;	end--select dbo.keszTermekE(1000)--select dbo.keszTermekE(1001)goalter table wo_mstradd constraint CK_WO_MSTR_Kesz_Termek_E check(dbo.keszTermekE(wo_part) = 1);